    function [X,S]=rand(h,nSamples)    %[X,S]=rand(h,nSamples);generates a random sequence of data    %from a given Hidden Markov Model.    %    %Input:    %h=         HMM object    %nSamples=  maximum no of output samples (scalars or column vectors)    %    %Result:    %X= matrix or row vector with output data samples    %S= row vector with corresponding integer state values    %   obtained from the h.StateGen component.    %   nS= length(S) == size(X,2)= number of output samples.    %   If the StateGen can generate infinite-duration sequences,    %       nS == nSamples    %   If the StateGen is a finite-duration MarkovChain,    %       nS <= nSamples    %    %----------------------------------------------------    %Code Authors: Alessio Russo(911103-T192), Lars Lindemann (891113-4131)     %----------------------------------------------------        if numel(h)>1            error('Method works only for a single object');        end;        % create a state sequence using the markov chain        S = rand(h.StateGen,nSamples);        % create output by using output distribution of each state        for i=1:length(S)            X(:,i) = rand(h.OutputDistr(S(i)),1);        end    end